sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;	
		c.getMaxShownLabel = function(maxEntries, totalCount) {
		return "${First [0] of [1] shown}".replace('[0]', maxEntries).replace('[1]', totalCount);
	};	
}

sp_widget.css:
.list-group-item {
