sp_widget.client_script:
function($scope,spUtil,$sce,$rootScope) {
	var c = this;

	//update breadcrumbs
	$rootScope.$broadcast('sp.update.breadcrumbs', c.data.breadcrumbs);
	//permit content to be rendered in html
	$scope.data.u_content = $sce.trustAsHtml($scope.data.u_content);

	//fill in notes field with previous input
	$scope.notes = $scope.data.u_personal_notes;

	//sends event to server that module is started or completed
	c.nextState = function(action){
		c.data.action = action;
		c.server.update().then(function() {
			c.data.action = undefined;
			spUtil.update($scope);
			$scope.data.u_content = $sce.trustAsHtml($scope.data.u_content);
		})
	}	
	c.saveNotes = function(action){
		c.data.action = action;
		c.data.notes = $scope.notes;
		c.server.update().then(function() {
			c.data.action = undefined;
			spUtil.update($scope);
		})
	}	
}

sp_widget.css:
.list-group-item {
  padding: 5px;
  border: none;
}

.summary {
	font-weight: bold;
}

.notes{
  flex: 1 1 auto;
  width: 100%;
  height: 100px;
  min-width: 100px;
  overflow-wrap: break-word;
  word-wrap: break-word;
  vertical-align: top;
}

sp_widget.script:
(function() {
	data.action = "";
	var tf_module = $sp.getParameter('sys_id');
	var z = new GlideRecord('u_module_instance');
	z.get(tf_module);
	var user = gs.getUserID();
	//var a = {};
	$sp.getRecordValues(data, z, 'number,short_description,description,sys_id,u_content,u_number_of_available_places,u_personal_notes');
	data.status = z.state.getValue();
	$sp.getRecordDisplayValues(data, z, 'u_instructor,u_formation_type');
	data.state = z.state.getDisplayValue();
	data.title = z.short_description.getValue();
	data.u_support = z.u_support.getDisplayValue();
	data.u_supportlink = z.u_support.getValue();
	data.u_supporttitle =  z.u_support.short_description.getValue();
	if(z.u_schedule)
		data.u_schedule = z.u_schedule.getGlideObject().getLocalDate().getDisplayValue();
	if(z.u_start){
		data.u_start = z.u_start.getGlideObject().getLocalDate().getDisplayValue();
		var startTime = z.u_start.getValue();
		var sTimeArray = startTime.split(' ');
		data.u_start_time = sTimeArray[1];
	}
	if(z.u_end){
		data.u_end = z.u_end.getGlideObject().getLocalDate().getDisplayValue();
		var endTime = z.u_end.getValue();
		var sEndTimeArray = endTime.split(' ');
		data.u_end_time = sEndTimeArray[1];
	}
	data.duration = z.u_duration_resolution.getDisplayValue();
	//Breadcrumbs
	var rec = z.u_from_training.getRefRecord();	
	data.breadcrumbs = [{label: data.title, url: '#'}];
	data.breadcrumbs.unshift({label: rec.getDisplayValue(), url: '?id=tf_modins&sys_id=' + rec.getUniqueValue()});
	data.allmyti = gs.getMessage("All My Trainings");
	data.breadcrumbs.unshift({label: data.allmyti, url: '?id=tf_allmyti'});


	if (!input){
		return;
	}
	else if (input.action){
		if (input.action == 'start' || input.action == 'complete'){
			z.state +=1 ;
		data.state = z.state.getDisplayValue();}
		// KAS : personal notes input
		else if (input.action == 'saveNotes'){
			z.u_personal_notes = input.notes;
		}

		z.update();
	}
})()
