sp_widget.script:
(function() {
	data.articles = [];
	var user = gs.getUserID();
	var z =new GlideRecord('u_training_instance');
	z.addQuery("assigned_to", user);//state is published
	z.addQuery("u_state","!=",0)//Removing Cancelled trainings
	z.orderBy("u_state");
	z.query();
	data.title = gs.getMessage("All My Trainings");
	data.breadcrumbs = [{label: data.title, url: '#'}];


	while (z.next()) {
		var a = {};
		$sp.getRecordValues(a, z, 'short_description,sys_id,u_formation_type,number,u_summary');
		$sp.getRecordDisplayValues(a, z, 'u_state');
		a.u_summary = z.u_summary.substring(0,55);
		if(z.u_schedule)
		a.u_schedule = z.u_schedule.getGlideObject().getLocalDate().getDisplayValue();
		if(z.u_schedule_end)
		a.u_schedule_end = z.u_schedule_end.getGlideObject().getLocalDate().getDisplayValue();
		if(z.u_end_validation_date)
		a.u_end_validation_date = z.u_schedule_end.getGlideObject().getLocalDate().getDisplayValue();
		a.u_owner = z.u_owner.getDisplayValue();
		a.u_author = z.u_author.getDisplayValue();
		a.u_percent = z.u_percent.getDisplayValue();
		data.articles.push(a);
	}
})()
