sp_widget.client_script:
function($sce) {
  /* widget controller */
  var c = this;

	//update breadcrumbs
	$rootScope.$broadcast('sp.update.breadcrumbs', c.data.breadcrumbs);

	c.data.u_content = $sce.trustAsHtml(c.data.u_content);
}

sp_widget.script:
(function() {
	var tf_module = $sp.getParameter('sys_id');
	var z = new GlideRecord('u_module_definition');
	z.get(tf_module);
	var user = gs.getUserID();
	//var a = {};
	$sp.getRecordValues(data, z, 'number,short_description,description,sys_id,u_formation_type,u_content,u_number_of_available_places,u_completion_time,u_site,u_number_of_available_places');
	$sp.getRecordDisplayValues(data, z, 'u_instructor');
	if(z.u_schedule)
		data.u_schedule = z.u_schedule.getGlideObject().getLocalDate().getDisplayValue();
	data.u_support = z.u_support.getDisplayValue();
	data.u_supportlink = z.u_support.getValue();
	data.u_supporttitle =  z.u_support.short_description.getValue();

	data.title = z.short_description.getValue();

	//Breadcrumbs
	//var rec = z.u_from_training.getRefRecord();	
	data.breadcrumbs = [{label: data.title, url: '#'}];
	//data.breadcrumbs.unshift({label: rec.getDisplayValue(), url: '?id=tf_modules&sys_id=' + rec.getUniqueValue()});
	data.allti = gs.getMessage("All Trainings");
	data.breadcrumbs.unshift({label: data.allti, url: '?id=tf_trainings'});
})()
