sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	//We only run this script when creating a new regstration, we do not want to override old records
	if(!g_form.isNewRecord())  return;

	else{

		var userList = g_form.getValue("guest_list");
		var guestTab = userList.split(',');

		var groupList = g_form.getValue("group_list");
		var groupTab = groupList.split(',');

		for (var j = 0; j<groupTab.length; j++){

			var userGrp = new GlideRecord('sys_user_grmember');
			userGrp.addQuery('group',groupTab[j]);
			userGrp.query();

			while (userGrp.next()){
				guestTab.push(userGrp.user.getValue("sys_id"));
			}
		}

// 		var arrayUtil = new ArrayUtil();
// 		var guestsList = arrayUtil.unique(guestTab);

		//We check if the array does not contain null rows to prevent void invitations
		var nullIndex = guestTab.indexOf('');
		if(nullIndex != '-1'){
			guestTab.splice(guestTab.indexOf(''),1);
		}
		g_form.setValue('number_of_guest', guestTab.length);
	}
}
