sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	//We only run this script when creating a new regstration, we do not want to override old records
	if(!g_form.isNewRecord())  return; 

	//We get the current event informations
	if(g_form.getValue('event') != ''){
		var grEvt = new GlideRecord ('x_206907_devo_tf_events_list');
		grEvt.get(g_form.getValue('event'));

		//We keep the difference between the current invintations total and the event capacity
		var cap = grEvt.capacity - grEvt.invitations;

		g_form.setValue('event_capacity', cap);
		g_form.addDecoration('event_capacity','icon-alert','Check Event Capacity before Submiting','color-red');

		//If the event is already at full capacity, we inform the user, and invite him to fill in an other event
		if(cap == 0){
			if(confirm("This Event is full already. \n Please choose another.")){
				g_form.setValue('event', null);
			}
		}

		//Else, we just inform the user how many more guest the event can accepts
		else{
			alert("Beware, this event can only accept " + cap + " more guests. Adapt your guests list in consideration.");
		}
	}
	else {
		g_form.setValue('event_capacity', '');
	}
}
