sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	if(!g_form.isNewRecord() || isLoading)  return;

	var ga = new GlideAjax('x_206907_devo_tf.guestCount');
	ga.addParam("sysparm_name", "count");
	ga.addParam("sysparm_event", newValue);
	ga.getXML("guestCountParse");
	//ga.getXMLWait();

	function guestCountParse(response) {
		alert('hi, this is the Parse');
		var answer = response.responseXML.getElementsByTagName("answer");

		var cap = answer[0].getAttribute("capacity");
		//	if (ga.getAnswer()){
			// 	var cap = ga.getAnswer();
			alert('capacity : '+ cap);

			g_form.setValue('event_capacity', cap);
			g_form.addDecoration('event_capacity','icon-alert','Check Event Capacity before Submitting','color-red');

			if(cap == 0){
				if(confirm("This Event is full already. \n Please choose another.")){
					g_form.clearValue('event');
				}
			}

			//Else, we just inform the user how many more guest the event can accepts
			else{
				alert("Beware, this event can only accept " + cap + " more guests. Adapt your guests list in consideration.");
			}
		}
	}
