var guestCount = Class.create();
guestCount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	count:function() {

		var eventAjax = this.getParameter('sysparm_event');
		gs.log("event : " + eventAjax,"TVA");
		if(eventAjax != ''){
			var grEvt = new GlideRecord ('x_206907_devo_tf_events_list');
			grEvt.get(eventAjax);

			var cap = grEvt.getValue("capacity") - grEvt.getValue("invitations");
// 			return cap;
					var answer = this.newItem('answer');
				   	answer.setAttribute('capacity',cap);

		}
		else {
			var value = '';
// 			return value;
					var answer = this.newItem('answer');
				   	answer.setAttribute('capacity',value);
		}
	}

});
