	// 2 - Je crée une training instance qui reprend les infos du training sélectionné

	//A - Je crée la training instance avec les infos de la training definition
	var ti = new GlideRecord('x_206907_devo_tf_training_instance');	
	ti.initialize();
	ti.assigned_to = current.user;
	ti.author =  current.training.author;
	ti.short_description = current.training.short_description;
	ti.subject = current.training.subject;
	ti.summary = current.training.summary;
	ti.description = current.training.description;
	ti.exam = current.training.exam;
	ti.formation_type = current.training.formation_type;
	ti.manager = current.manager;	
	ti.schedule = current.training.schedule;
	ti.schedule_end = current.training.schedule_end;
	var newTrainingInstance = ti.insert();
	current.instance = newTrainingInstance;

	//B - Je crée les modules instances associés

	//Je recherche les modules associés aux trainings :
	var td = new GlideRecord('x_206907_devo_tf_training_definition');
	td.get(current.training);

	// Je reprend cette info pour trouver les modules correspondants

	// Je remonte la liste des modules
	var trainingModule = new GlideRecord('x_206907_devo_tf_m2m_module_def_training_def');
	trainingModule.addQuery('training_definition',td.sys_id);
	trainingModule.orderBy('sys_created_on');
	trainingModule.query();

	while (trainingModule.next()){
		var md = new GlideRecord('x_206907_devo_tf_module_definition');	
		md.get(trainingModule.module_definition);
		
		// On le copie en tant qu'instance
		var mi = new GlideRecord('x_206907_devo_tf_module_instance');
		mi.initialize();
		mi.assigned_to = current.user;
		mi.instructor = md.instructor;
		mi.short_description = md.short_description;
		mi.description = md.description;
		mi.formation_type = md.formation_type;
		mi.site = md.site;
		mi.number_of_available_places = md.number_of_available_places;
		mi.completion_time = md.completion_time;
		mi.content = md.content;
		mi.author = md.author;
		mi.support = md.support;
		mi.from_training = newTrainingInstance; // On lie le Module Instance au Training Istance		
		var newModuleInstance = mi.insert();	
	}
	gs.addInfoMessage("User : " + current.user.getDisplayValue() + " has been successfully enrolled in the training : " + current.training.getDisplayValue());

