// On filtre les users du groupe
var userGrp = new GlideRecord('sys_user_grmember');
userGrp.addQuery('group',current.group);
userGrp.query();

//gs.log("Number of people in the group : " + userGrp.getRowCount(),"KAS");


	while (userGrp.next()){
		
		var test = new GlideRecord('x_206907_devo_tf_inscription');
		test.addQuery("user", userGrp.user);
		test.addQuery("training", current.training);
		test.query();
			
		if (test.hasNext()) {
			gs.addErrorMessage("This user : " + userGrp.user.getDisplayValue()  + " is already enrolled in this Training : " + current.training.getDisplayValue());
			}
		
		else {
		
			//A - Je crée la training instance avec les infos de la training definition
			var ti = new GlideRecord('x_206907_devo_tf_training_instance');	
			ti.initialize();
			ti.assigned_to = userGrp.user;
			ti.author =  current.training.author;
			ti.subject = current.training.subject;
			ti.short_description = current.training.short_description;
			ti.description = current.training.description;
			ti.summary = current.training.summary;
			ti.exam = current.training.exam;
			ti.training_type = current.training.training_type;
			ti.manager = current.manager;	
			ti.schedule = current.training.schedule;
			ti.schedule_end = current.training.schedule_end;
			
			var newTrainingInstance = ti.insert();

			// On lie l'instance à l'inscription
			var ins = new GlideRecord('x_206907_devo_tf_inscription');
			ins.initialize();
			ins.user = userGrp.user;
			ins.instance = newTrainingInstance;
			ins.group = current.group;
			ins.training = current.training;
			ins.manager = current.manager;
			ins.approval = "approved";
			ins.insert();
			
			//B - Je crée les modules instances associés

			//Je recherche les modules associés aux trainings :
			var td = new GlideRecord('x_206907_devo_tf_training_definition');
			td.get(current.training);

			// Je remonte la liste des modules
			var trainingModule = new GlideRecord('x_206907_devo_tf_m2m_module_def_training_def');
			trainingModule.addQuery('training_definition',td.sys_id);
			trainingModule.orderBy('sys_created_on');
			trainingModule.query();

			while (trainingModule.next()){
				var md = new GlideRecord('x_206907_devo_tf_module_definition');	
				md.get(trainingModule.module_definition);
				
				// On le copie en tant qu'instance
				var mi = new GlideRecord('x_206907_devo_tf_module_instance');
				mi.initialize();
				mi.assigned_to = userGrp.user;
				mi.instructor = md.instructor;
				mi.short_description = md.short_description;
				mi.description = md.description;
				mi.training_type = md.training_type;
				mi.site = md.site;
				mi.number_of_available_places = md.number_of_available_places;
				mi.completion_time = md.completion_time;
				mi.content = md.content;
				mi.support = md.support;
				mi.author = md.author;
				mi.from_training = newTrainingInstance; // On lie le Module Instance au Training Instance		
				var newModuleInstance = mi.insert();	
			}//end while of module instance creation
			gs.addInfoMessage("User : " + userGrp.user.getDisplayValue() + " has been successfully enrolled in the training : " + current.training.getDisplayValue());
		}//end test user inscription
	}//end while of group user loop
	gs.addInfoMessage("Group : " + userGrp.group.getDisplayValue() + " was successfully enrolled in the training : " + current.training.getDisplayValue());
	GlideList2.get("inscription").refresh();

